INSERT INTO `loaisanpham` (`MaLoaiSanPham`, `TenLoaiSanPham`, `BiXoa`, `GioiThieu`, `HinhURL`) VALUES
(17, 'Nhóm Cá', 0, 'Cá là một thực phẩm giàu chất dinh dưỡng và chữa được nhiều bệnh, là nguồn cung cấp protein cho con người', 'cate_1383975877.jpg'),
(19, 'Nhóm Ốc - Sò - Hào', 0, 'Ốc, sò, hào có rất nhiều loại: ốc giác, móng tay, cánh tiên, sò huyết, sò điệp... Mỗi loại một đặc điểm riêng và cách chế biến cũng đa dạng', 'cate_1384847495.jpg'),
(21, 'Nhóm Mực ', 0, 'Mực là món ăn rất thích hợp với mùa hè. Mực có thể xào theo nhiều kiểu khác nhau mà kiểu nào cũng mang lại độ giòn và hấp dẫn. ', 'cate_1384847136.jpg'),
(22, 'Nhóm Cua', 0, 'Thịt cua là loại thực phẩm giàu chất khoáng, ít chất béo và calo. Thịt cua giúp phòng ngừa loãng xương, thiếu máu, viêm khớp ...', 'cate_1383975936.png'),
(23, 'Nhóm Tôm', 0, 'Là loại thực phẩm có giá trị dinh dưỡng rất cao, chứa nhiều chất đạm, các vitamin và nguyên tố vi lượng. Tôm được tôn vinh là vua của các loại hải sản bởi giá trị dinh dưỡng cao.', 'cate_1383975946.png'),
(24, 'Nhóm Lươn', 0, 'Lươn là món ăn đặc sản của người việt nam. Ở nam bộ có nhiều phương thức chế biến từ đơn giản đến cầu kỳ như lươn xào lăn, lươn xé phay, lẩu lươn... tại miền bắc, món ăn nổi tiếng nhất có lẽ là miến lươn', 'cate_1384846945.jpg');

